package FloatAndDouble;

import java.math.BigDecimal;

public class FloatDouble {

    public static void main(String[] args) {
        float myMinFloatValue = Float.MIN_VALUE;
        float myMaxFloatValue = Float.MAX_VALUE;

        System.out.println("Float min: " + myMinFloatValue + " Float max: " + myMaxFloatValue);

        double myMinDoubleValue = Double.MIN_VALUE;
        double myMaxdoubleValue = Double.MAX_VALUE;

        System.out.println("double min: " + myMinDoubleValue + " double max: " + myMaxdoubleValue);

        int myIntValue = 5 / 3;
        float myFloutValue = 5f / 3f;
        double myDoubleValue = 5d / 3d;

        System.out.println("MyIntvalue = " + myIntValue);
        System.out.println("MyFloutValue = " + myFloutValue);
        System.out.println("MyDoubleValue = " + myDoubleValue);

        double onePound = 0.45359237d;
        double pounds = 200;
        double kg = pounds * onePound;

        System.out.println(kg);
        BigDecimal big = BigDecimal.valueOf(4.);
        double pi = 3.1415927d;
        double anotherNumber = 3_000_000.4_890d;
        System.out.println(anotherNumber);


    }

}
